import React, { Component } from 'react';
import commerce from '../lib/commerce';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { library } from '@fortawesome/fontawesome-svg-core'
import { faShoppingBag, faTimes } from '@fortawesome/free-solid-svg-icons'
import PropTypes from 'prop-types';
import { AppContext } from '../context/state.js'

import Hero from '../components/Hero';
import ProductsList from '../components/ProductsList';
import Cart from '../components/Cart';

library.add(faShoppingBag, faTimes)

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      merchant: {},
      products: [],
    }
  }

  static contextType = AppContext;

  componentDidMount() {
    this.fetchMerchantDetails();
    this.fetchProducts();
    this.context.fetchCart();
    this.context.loadOrderFromLocalStorage();
  }

  /**
   * Fetch merchant details
   * https://commercejs.com/docs/sdk/full-sdk-reference#merchants
   */
  fetchMerchantDetails() {
    commerce.merchants.about().then((merchant) => {
      this.setState({ merchant: merchant });
    }).catch((error) => {
      console.log('There was an error fetch the merchant details', error)
    });
  }

  /**
   * Fetch products data from Chec and stores in the products data object.
   * https://commercejs.com/docs/sdk/products
   */
  fetchProducts() {
    commerce.products.list().then((products) => {
      this.setState({ products: products.data });
    }).catch((error) => {
      console.log('There was an error fetching the products', error);
    });
  }


  renderCartNav() {
    const { cart, isCartVisible } = this.context;

    return (
      <div className="nav">
        <div className="nav__cart" onClick={this.context.toggleCart}>
          {!isCartVisible ? (
            <button className="nav__cart-open">
              <FontAwesomeIcon size="2x" icon="shopping-bag" color="#292B83" />
              {cart !== null ? <span>{cart.total_items}</span> : ''}
            </button>
          ) : (
              <button className="nav__cart-close">
                <FontAwesomeIcon size="1x" icon="times" color="white" />
              </button>
            )}
        </div>
      </div>
    )
  }

  render() {
    const {
      products,
      merchant,
    } = this.state;

    return (
      <div className="app">
        <>
          <Hero
            merchant={merchant}
          />

          {this.renderCartNav()}
          
          {this.context.isCartVisible &&
            <Cart
              {...this.props}
              handleCaptureCheckout={this.handleCaptureCheckout}
            />
          }
          <ProductsList
            {...this.props}
            products={products}
          />
        </>
      </div>
    );
  }
};

export default App;

App.propTypes = {
  history: PropTypes.object,
};
