import React, { Component } from 'react';
import PropTypes from 'prop-types';
import CartItem from './CartItem';
import Link from 'next/link'
import { AppContext } from '../context/state.js'


class Cart extends Component {
    constructor(props) {
        super(props);

        this.handleEmptyCart = this.handleEmptyCart.bind(this);
        this.renderCart = this.renderCart.bind9(this);
    }

    static contextType = AppContext;


    handleEmptyCart() {
        this.context.handleEmptyCart();
    }

    renderEmptyCart() {
        const { cart } = this.context;
        if (cart.total_unique_items > 0) {
          return;
        }

        return (
          <p className="cart__none">
            You have no items in your shopping cart, start adding some!
          </p>
        );
    }

    renderCart() {
        const { cart } = this.context;
        if (cart.total_unique_items === 0) {
          return;
        }

        return (
          <>
            {cart.line_items.map(lineItem => (
              <CartItem
                item={lineItem}
                key={lineItem.id}
                {...this.props}
                className="cart__inner"
              />
            ))}
            <div className="cart__total">
              <p className="cart__total-title">Subtotal:</p>
              <p className="cart__total-price">{cart.subtotal.formatted_with_symbol}</p>
            </div>
            <div className="cart__footer">
              <button className="cart__btn-empty" onClick={this.handleEmptyCart}>Empty cart</button>
              <button 
onClick={()=> this.props.history.push('/checkout')}              >
                <a className="cart__btn-checkout">
                Checkout
                </a>
              </button>
            </div>
          </>
        );
    }

    render() {
        return (
          <div className="cart">
            <h4 className="cart__heading">Your Shopping Cart</h4>
            { this.renderEmptyCart() }
            { this.renderCart() }
          </div>
        );
    };
};

export default Cart;

Cart.propTypes = {
  cart: PropTypes.object,
  onUpdateCartQty: () => {},
  onRemoveFromCart: () => {},
  onEmptyCart: () => {},
  handleUpdateCartQty: PropTypes.func,
  history: PropTypes.object
};
