import { Component } from 'react';
import { Map, InfoWindow, Marker, GoogleApiWrapper } from 'google-maps-react';
import Pusher from 'pusher-js';
import axios from 'axios';


class Tracking extends Component {
    constructor(props) {
        super(props)
        this.state = { "latitude": 20, "longitude": 20, "accuracy": "1" };
        this.getUpdates = this.getUpdates.bind(this);
    }

    componentDidMount() {
        this.getUpdates();
    }

    getUpdates() {
        var pusher = new Pusher('4b368d79d76620bdd3a5', {
            authEndpoint: 'https://22e7a901651d.ngrok.io/pusher/auth',
            cluster: 'eu'
        });

        var channel = pusher.subscribe('private-test');

        channel.bind('client-driver-location', (data) => {

            this.setState(data);

            // axios.post('http://localhost:9000/api/new', {
            //     latitude: data.latitude,
            //     longitude: data.longitude,
            //     accuracy: data.accuracy
            // })
            //     .then(function (response) {
            //         console.log(response);
            //     })
            //     .catch(function (error) {
            //         console.log(error);
            //     });
        });

    }

    render() {
        const {latitude, longitude} = this.state;

        return (

            <Map google={this.props.google} zoom={100} center={{
                lat: latitude ,
                lng: longitude
            }}initialCenter={{
                lat: 40.854885,
                lng: -88.081807
              }}>
                <Marker onClick={this.onMarkerClick}
                    name="{'Current location'}" />

                <InfoWindow onClose={this.onInfoWindowClose}>
                    <div>
                        <h1>"this.state.selectedPlace.name"</h1>
                    </div>
                </InfoWindow>
            </Map>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: (process.env.GMAPSKEY)
})(Tracking);
